export const returnRewards = (customerTransactions) => {
    return customerTransactions.map(customer => {
        console.log('customer::', customer);
        return {
            name: Object.keys(customer)[0], october:calculatePoints(customer[0][0].October), november: 40, december: 40, totalPoints: 100
        }
    })
}

export const calculatePoints = (transactions) => {
    const initialPoints = 0;
    const totalPoints = transactions.reduce((sum, pointsPerTransaction) => sum + pointsPerTransaction,
    initialPoints);
    return totalPoints;
}
